'use strict';

function Context(app) {
    this.application = app;
}

Context.prototype.getService = function (){
    this.application.getService.apply(this.application, arguments);
};

Context.prototype.log = function (){
    this.application.getService('logger').log.apply({}, arguments);
};

Context.create = function (application) {
    return new Context(application);
};

module.exports = Context;
